import React, { useEffect } from 'react';
import {useNavigate} from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { inventory } from '../../Data/Inventory';
import { setSelectedInventory } from '../Redux/actions';

function InventoryTerms() {
    const dispatch = useDispatch();
    const navigate = useNavigate();
    const selectedInventory = useSelector(state => state.search.selectedInventory); // Sử dụng đúng đường dẫn đến selectedInventory

    useEffect(() => {
        console.log(selectedInventory);
    }, [selectedInventory]); // Sử dụng selectedInventory trong dependency array để theo dõi thay đổi

    function handleInventoryClick(inventory) {
        dispatch(setSelectedInventory(inventory));
        navigate('/traditional-search');
    }

    return (
        <div>
            <h1>Inventory Terms</h1>
            {inventory.map((inventory, index) => (
                <button key={index} onClick={() => handleInventoryClick(inventory)}>
                    {inventory}
                </button>
            ))}
        </div>
    );
}

export default InventoryTerms;
