import { provenience } from '../../Data/Provenience.js';

function groupProvenienceByFirstLetter(provenience) {
  const groups = {};

  provenience.forEach(item => {
    const firstLetter = item.charAt(0).toUpperCase(); // Chuyển chữ cái đầu tiên thành chữ hoa

    if (!groups[firstLetter]) {
      groups[firstLetter] = [];
    }

    groups[firstLetter].push(item);
  });

  // Sắp xếp các nhóm theo thứ tự chữ cái
  const sortedGroups = {};
  Object.keys(groups).sort().forEach(key => {
    sortedGroups[key] = groups[key];
  });

  return sortedGroups;
}

// Sử dụng function để nhóm các provenience theo chữ cái đầu tiên
const groupedProvenience = groupProvenienceByFirstLetter(provenience);

// Chuyển đổi object thành mảng các object để render
const items = Object.entries(groupedProvenience).map(([letter, values]) => ({
  letter,
  values: values.map(value => ({
    name: value,
    url: `testSearch.asp?searchBy=Provenience&txtValue=${value}&exact=true`
  }))
}));

// Sử dụng mảng items để render trong BasicSearch component
export default items;
