import React, { useState } from "react";

function SearchBar({ handleSearch, type, searchInput, setSearchInput, searchData, vaseData }) {
  const [matchedItems, setMatchedItems] = useState([]);
const [showSuggestions, setShowSuggestions] = useState(false);
  function levenshtein(a, b) {
    const matrix = [];

    for (let i = 0; i <= b.length; i++) {
        matrix[i] = [i];
    }

    for (let j = 0; j <= a.length; j++) {
        matrix[0][j] = j;
    }

    for (let i = 1; i <= b.length; i++) {
        for (let j = 1; j <= a.length; j++) {
            if (b.charAt(i - 1) === a.charAt(j - 1)) {
                matrix[i][j] = matrix[i - 1][j - 1];
            } else {
                matrix[i][j] = Math.min(
                    matrix[i - 1][j - 1] + 1,
                    matrix[i][j - 1] + 1,
                    matrix[i - 1][j] + 1
                );
            }
        }
    }

    return matrix[b.length][a.length];
}
function normalizeString(str) {
  return str
    .normalize('NFD') // chuyển đổi chuỗi sang dạng không dấu
    .replace(/[\u0300-\u036f]/g, '') // loại bỏ dấu
    .replace(/[!@#$%^&*()-]/g, '') // loại bỏ ký tự đặc biệt
    .replace(/\s+/g, '') // loại bỏ khoảng trắng
    .toLowerCase(); // chuyển đổi chuỗi sang chữ thường
}

const handleInputChange = (e) => {
  const inputVal = normalizeString(e.target.value);
  setSearchInput(inputVal);

  if (inputVal.trim() === "") {
    setMatchedItems([]);
    setShowSuggestions(true); // Hiển thị phần gợi ý khi input rỗng
    return;
  }

  if (!searchData) {
    console.error('searchData is undefined');
    return;
  }

  const matches = searchData
    .filter(item => typeof item === 'string' && normalizeString(item).startsWith(inputVal))
    .map(option => {
      const count = vaseData.filter(vase => vase["Provenience"] === option ).length;
      return { name: option, count };
    });

  setMatchedItems(matches);
  setShowSuggestions(true);
};

  const handleClickSearch = () => {
    handleSearch(searchInput);
  };

  return (
    <div className="search-bar-wrapper search-bar-wrapper--advanced search-bar-wrapper--advanced-single">
      <div className="header__search header__search--advanced">
        <div className="header__search-input-wrap header__search-input-wrap--advanced">
          <input
            type="text"
            className="header__search-input header__search-input--advanced"
            placeholder={`Search ${type} ...`}
            value={searchInput}
            onChange={handleInputChange}
          />
        </div>
        <button
          className="header__search-btn header__search-btn--advanced"
          onClick={handleClickSearch}
        >
          <i className="header__search-btn-icon fas fa-search"></i>
        </button>
      </div>
    </div>
  );
}

export default SearchBar;
