//InventoryGroups.js

import {inventory} from '../../Data/Inventory.js';

function groupInventoryByFirstLetter(inventory) {
  const groups = {};

  inventory.forEach(item => {
    const firstLetter = item.charAt(0).toUpperCase(); // Chuyển chữ cái đầu tiên thành chữ hoa

    if (!groups[firstLetter]) {
      groups[firstLetter] = [];
    }

    groups[firstLetter].push(item);
  });

  // Sắp xếp các nhóm theo thứ tự chữ cái
  const sortedGroups = {};
  Object.keys(groups).sort().forEach(key => {
    sortedGroups[key] = groups[key];
  });

  return sortedGroups;
}

const groupedInventory = groupInventoryByFirstLetter(inventory);

// Chuyển đổi object thành mảng các object để render
const items = Object.entries(groupedInventory).map(([letter, values]) => ({
  letter,
  values: values.map(value => ({
    name: value,
    url: `testSearch.asp?searchBy=Shape&txtValue=${value}&exact=true`
  }))
}));

// Sử dụng mảng items để render trong BasicSearch component
export default items;
