// actions.js

export const saveSearchResult = (result) => ({
    type: 'SAVE_SEARCH_RESULT',
    payload: result,
    savePrevious: true,
});

export const resetSearchResult = () => ({
    type: 'RESET_SEARCH_RESULT',
});

export const saveSearchData = (data) => ({
    type: 'SAVE_SEARCH_DATA',
    payload: data,
});
