import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { shapes } from '../Data/Shapes';
import { setSelectedShape } from '../Redux/actions';

function ShapeTerms() {
    const dispatch = useDispatch();
    const selectedShape = useSelector(state => state.search.selectedShape); // Sử dụng đúng đường dẫn đến selectedShape

    useEffect(() => {
        console.log(selectedShape);
    }, [selectedShape]); // Sử dụng selectedShape trong dependency array để theo dõi thay đổi

    function handleShapeClick(shape) {
        dispatch(setSelectedShape(shape));
    }

    return (
        <div>
            <h1>Shape Terms</h1>
            {shapes.map((shape, index) => (
                <button key={index} onClick={() => handleShapeClick(shape)}>
                    {shape}
                </button>
            ))}
        </div>
    );
}

export default ShapeTerms;
