import React, { useEffect } from 'react';
import {useNavigate} from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { provenience } from '../Data/Provenience';
import { setSelectedProvenience } from '../Redux/actions';

function ProvenienceTerms() {
    const dispatch = useDispatch();
    const navigate = useNavigate();
    const selectedProvenience = useSelector(state => state.search.selectedProvenience); // Sử dụng đúng đường dẫn đến selectedProvenience

    useEffect(() => {
        console.log(selectedProvenience);
    }, [selectedProvenience]); // Sử dụng selectedProvenience trong dependency array để theo dõi thay đổi

    function handleProvenienceClick(Provenience) {
        dispatch(setSelectedProvenience(provenience));
        navigate('/traditional-search');
    }

    return (
        <div>
            <h1>Provenience Terms</h1>
            {provenience.map((provenience, index) => (
                <button key={index} onClick={() => handleProvenienceClick(provenience)}>
                    {provenience}
                </button>
            ))}
        </div>
    );
}

export default ProvenienceTerms;
